import React from 'react';
import './Loading'
import './App.css';
import Loading from './Loading';


function LangList(props) {
  return (
    <div>
      <ul>
        {props.list.map((e) =>
          <li onClick={ () => props.onfetchPopularRepos(e)}> {e} </li>
        )}
      </ul>
    </div>
  )
}

function fetchPopularRepos(language) {
  // "language" can be "javascript", "ruby", "python", or "all"
  const encodedURI = encodeURI(`https://api.github.com/search/repositories?q=stars:>1+language:${language}&sort=stars&order=desc&type=Repositories`)

  return fetch(encodedURI)
    .then((data) => data.json())
    .then((repos) => console.log(repos.items))
    .catch((error) => {
      console.warn(error)
      return null
    });
}


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      list: ['Paython', 'Javascript', 'Ruby']
    }
    this.fetchPopularRepos = this.fetchPopularRepos.bind(this)
  }

  render() {
    return (
      <div>
        <LangList list={this.state.list} onfetchPopularRepos = {this.fetchPopularRepos}/>
      </div>
    )
  }
}

export default App;
