import React from 'react';
import './Loading'
import './App.css';
import Loading from './Loading';


function LangList(props) {
  return (
    <div>
      <ul>
        {props.list.map((e) =>
          <li
            onClick={() => props.onfetchPopularRepos(e)} key={e} > {e}
          </li>
        )}
      </ul>
    </div>
  )
}


function Results(props) {
  return (
    <div>
      <ul>
        {props.items.map((e) =>
          <li key={e.id} > {e.full_name}  </li>
        )}
      </ul>
    </div>
  )
}


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      list: ['Paython', 'Javascript', 'Ruby'],
      loading: false
    }
    this.fetchPopularRepos = this.fetchPopularRepos.bind(this)
  }

  fetchPopularRepos(language) {
    // "language" can be "javascript", "ruby", "python", or "all"
    const encodedURI = encodeURI(`https://api.github.com/search/repositories?q=stars:>1+language:${language}&sort=stars&order=desc&type=Repositories`)
    this.setState({
      loading: true
    })
    return fetch(encodedURI)
      .then((data) => data.json())
      .then((repos) => {
        console.log(repos.items)
        this.setState({
          results: repos.items,
          loading: false
        })
      })
      .catch((error) => {
        console.warn(error)
        return null
      });
  }


  render() {

    if (this.state.loading === true) {
      return <Loading />
    } 



    return (
      <div>
        <LangList list={this.state.list} onfetchPopularRepos={this.fetchPopularRepos} />
      </div>
    )
  }
}

export default App;
